import { Squash as Hamburger } from "hamburger-react";
import React, { useState } from "react";

// dropdown filter data
const filterdata = {
  Batch: [2019, 2020, 2021, 2022],
  Branch: ["Engineering", "Management", "Pharamacy", "Agriculture"],
  Package: ["Highest", "Lowest", "Newest", "Oldest"],
};

const data = {
  Batch: {
    id: 1,
    2019: 2019,
    2020: 2020,
    2021: 2021,
    2022: 2022,
  },
  Branch: {
    id: 2,
    enginnering: "Engineering",
    management: "Management",
    pharamacy: "Pharamacy",
    agriculture: "Agriculture",
  },
  Package: {
    id:3,
    highest:"Highest",
    lowest:"Lowest",
    newest:"Newe"
  }
};

const Filter = () => {
  // function to delete chips
  function hide() {
    document.getElementById("single-filter-elem").style.display = "none";
  }
  return (
    // Component
    // ⬇️⬇️⬇️⬇️
    <div id="filter">
      <div id="filter-box" className="flex justify-between items-center">
        {/* Filter chip */}
        <div
          id="filtered-elements"
          className="flex overflow-scroll no-scrollbar"
        >
          {/* <div
            id="single-filter-elem"
            className="flex border-2 border-black shadow-custom rounded-md m-1 p-1 bg-my-pink"
          >
            <div>
              <p className="font-bold">Management</p>
            </div>
            <div
              className="px-2 font-bold text-gray-700 hover:text-black hover:cursor-pointer"
              onClick={hide}
            >
              X
            </div>
          </div> */}
        </div>
        {/* Hamburger menu button */}
        <div id="menu-button">
          <Hamburger
            rounded
            size={24}
            onToggle={(toggled) => {
              if (toggled) {
                // open a menu
                document
                  .getElementById("filter-dropdown")
                  .classList.remove("invisible");
              } else {
                // close a menu

                document
                  .getElementById("filter-dropdown")
                  .classList.add("invisible");
              }
            }}
          />
        </div>
      </div>
      {/* Dropdown */}
      <div
        id="filter-dropdown"
        className="flex delay-200 invisible justify-end"
      >
        <div
          id="dropdown"
          className=" flex p-2 border-black border-2 rounded-md m-3 flex-col bg-my-pink "
        >
          <div className=" flex flex-col md:flex-row">
            <div
              id="single-filter"
              className="m-2 border-2 shadow-custom p-4 border-black rounded bg-white"
            >
              <form>
                <div className="flex flex-col">
                  <div>
                    <fieldset>
                      <legend className="font-bold text-lg">Batch</legend>
                      <input
                        type="radio"
                        name="action"
                        id="track"
                        value="track"
                        className="mr-1 bg-white "
                      />
                      <label for="track">2019</label>
                      <br />
                    </fieldset>
                  </div>
                </div>
              </form>
            </div>
          </div>

          <div id="filter-button" className="flex m-2 items-baseline">
            <p className="underline text-gray-800 hover:text-black hover:cursor-pointer">
              Clear
            </p>
            <button className="border-2 rounded-md border-black py-1 px-2 mx-2 bg-white font-black shadow-custom hover:shadow-none ">
              Apply
            </button>
          </div>
        </div>
      </div>
    </div>
  );
};

export default Filter;
