import { useState } from "react";
import Filterdata from "./Filterdata";

const Dropdown = () => {
  const [name, setName] = useState("");
  const [allData, setAllData] = useState([]);

  const handleChange = (event) => {
    setName(event.target.value);
  };
  const handleAdd = () => {
    if (name.length !== 0) {
      setAllData((newData) => [...newData, name]);
    }
  };
  console.log(allData);
  return (
    <div
      id="filter-dropdown"
      className="flex transition-all duration-150 ease-in delay-500 invisible justify-end"
    >
      <div
        id="dropdown"
        className=" flex p-2 border-black border-2 rounded-md m-3 flex-col bg-my-pink "
      >
        <div className="flex flex-col md:flex-row ">
          {Filterdata.map((h) => (
            <div className=" flex flex-col">
              <div
                id="single-filter"
                className="m-2 border-2 shadow-custom p-4 border-black rounded bg-white"
              >
                <form>
                  <div className="flex flex-col">
                    <div>
                      <fieldset>
                        <legend key={h.heading} className="font-bold text-lg">
                          {h.heading}
                        </legend>
                        {h.list.map((l) => (
                          <>
                            <label key={l} id="label" htmlFor={l}>
                              <input
                                type="radio"
                                name="name"
                                id={l}
                                value={l}
                                className="mr-1 bg-white "
                                onChange={handleChange}
                                onClick={handleAdd}
                              />
                              {l}
                            </label>
                            <br />
                          </>
                        ))}
                      </fieldset>
                    </div>
                  </div>
                </form>
              </div>
            </div>
          ))}
        </div>
        <div id="filter-button" className="flex m-2 items-baseline">
          <p className="underline text-gray-800 hover:text-black hover:cursor-pointer">
            Clear
          </p>
          <button
            onClick={handleAdd}
            className="border-2 rounded border-black py-1 px-2 mx-2 bg-white font-black shadow-custom transition-all duration-150 ease-out delay-0 focus:shadow-none focus:translate-x-1 focus:translate-y-1  "
          >
            Apply
          </button>
        </div>
      </div>
    </div>
  );
};

export default Dropdown;
