import React, { useState, useEffect } from 'react';

const Navbar = () => {
  const [stickyClass, setStickyClass] = useState('relative');

  useEffect(() => {
    window.addEventListener('scroll', stickNavbar);

    return () => {
      window.removeEventListener('scroll', stickNavbar);
    };
  }, []);

  const stickNavbar = () => {
    if (window !== undefined) {
      let windowHeight = window.scrollY;
      windowHeight > 500 ? setStickyClass('fixed top-0 left-0 z-50') : setStickyClass('relative');
    }
  };
  return (
    // DaisyUI Navbar ðŸ”°ðŸ”°ðŸ”°
    <div className="navbar bg-base-100 shadow-md rounded-lg ${stickyClass}">
      <div className="navbar-start">
        <a className="btn btn-ghost normal-case text-2xl text-primary">
          MateLy
        </a>
      </div>
      <div className="navbar-end">
        <a className="btn bg-primary shadow-md">Admin</a>
      </div>
    </div>
  );
};
export default Navbar;
