      id="dropdown"
      className=" flex p-2 border-black border-2 rounded-md m-3 flex-col bg-my-pink "
    >
      <div className=" flex flex-col md:flex-row">
        <div
          id="single-filter"
          className="m-2 border-2 shadow-custom p-4 border-black rounded bg-white"
        >
          <form>
            <div className="flex flex-col">
              <div>
                <fieldset>
                  <legend className="font-bold text-lg">{d.heading}</legend>
                  <input
                    type="radio"
                    name="action"
                    id="track"
                    value="track"
                    className="mr-1 bg-white "
                  />
                  <label for="track">{d.list}</label>
                  <br />
                </fieldset>
              </div>
            </div>
          </form>
        </div>
      </div>

      <div id="filter-button" className="flex m-2 items-baseline">
        <p className="underline text-gray-800 hover:text-black hover:cursor-pointer">
          Clear
        </p>
        <button className="border-2 rounded-md border-black py-1 px-2 mx-2 bg-white font-black shadow-custom hover:shadow-none ">
          Apply
        </button>
      </div>
    </div>
  </div>
));

