// import Filteredchip from "./Filteredchip";
import Burger from "./Burger";
// import Dropdown from "./Dropdown";
import Filterdata from "./Filterdata";
import { useState } from "react";

const Filter = () => {
  const [name, setName] = useState("");
  const [allData, setAllData] = useState([]);

  const handleChange = (event) => {
    setName(event.target.value);
  };
  const handleAdd = () => {
    if (name.length !== 0) {
      setAllData((newData) => [...newData, name]);
    }
  };
  console.log(allData);
  const clearfil = () => {
    document.location.reload();
  };
  // console.log(allData);
  return (
    <div id="filter">
      <div id="filter-box" className="flex justify-between items-center">
        {/* <Filteredchip /> */}
        <div
          id="filtered-elements"
          className="flex overflow-scroll no-scrollbar"
        >
          {allData.map((e) => (
            <div
              id="single-filter-elem"
              className="flex border-2 border-black shadow-custom rounded m-1 p-1 bg-my-pink transition-all duration-150 ease-out delay-0 hover:shadow-none hover:translate-x-1 hover:translate-y-1 "
            >
              <div>
                <p className="font-bold">{e}</p>
              </div>
              <div className="px-2 font-bold text-gray-700 hover:text-black hover:cursor-pointer">
                X
              </div>
            </div>
          ))}
        </div>
        <Burger />
      </div>
      {/* <Dropdown /> */}
      <div
        id="filter-dropdown"
        className="flex transition-all duration-150 ease-in delay-500 invisible justify-end"
      >
        <div
          id="dropdown"
          className=" flex p-2 border-black border-2 rounded-md m-3 flex-col bg-my-pink "
        >
          <div className="flex flex-col md:flex-row ">
            {Filterdata.map((h) => (
              <div className=" flex flex-col">
                <div
                  id="single-filter"
                  className="m-2 border-2 shadow-custom p-4 border-black rounded bg-white"
                >
                  <form>
                    <div className="flex flex-col">
                      <div>
                        <fieldset>
                          <legend key={h.heading} className="font-bold text-lg">
                            {h.heading}
                          </legend>
                          {h.list.map((l) => (
                            <>
                              <label key={l} id="label" htmlFor={l}>
                                <input
                                  type="radio"
                                  name="name"
                                  id={l}
                                  value={l}
                                  className="mr-1 bg-white "
                                  onChange={handleChange}
                                  onClick={handleAdd}
                                />
                                {l}
                              </label>
                              <br />
                            </>
                          ))}
                        </fieldset>
                      </div>
                    </div>
                  </form>
                </div>
              </div>
            ))}
          </div>
          <div id="filter-button" className="flex m-2 items-baseline">
            <p
              onClick={clearfilter}
              className="underline text-gray-800 hover:text-black hover:cursor-pointer"
            >
              Clear
            </p>
            <button
              onClick={handleAdd}
              className="border-2 rounded border-black py-1 px-2 mx-2 bg-white font-black shadow-custom transition-all duration-150 ease-out delay-0 focus:shadow-none focus:translate-x-1 focus:translate-y-1  "
            >
              Apply
            </button>
          </div>
        </div>
      </div>
    </div>
  );
};

export default Filter;
